    private float shakeThreshold = 3f; //   ,     
    private int shakeCount = 0;
    public float interval = 0.5f;  //0.5f  
    public float doubleclickedtime = 0f;
    public bool isdoubleclicked = false;


    public void Update() 
    {
        if (testplayer.PV.IsMine )
        {
            Vector3 acceleration = Input.acceleration;
            float currentMagnitude = acceleration.sqrMagnitude;
            float thresholdValue = shakeThreshold;
            //      
            if (currentMagnitude >= thresholdValue)
            {
                //Debug.Log("Shake detected! Total shakes: " + shakeCount);
                float currentTime = Time.time;
                interval = 0.5f;
                if ((currentTime - doubleclickedtime) < interval)
                {
                    shakeCount++;
                    if (shakeCount >= 2)
                    {
                        doubleclickedtime = -1.0f;
                        //testtts.Instance.alltts("  ");
                        if (SmartSecurity.GetInt("checkuserevent") == 0)
                        {
                           
                            JoyStickTTS.Instance.audioSource.Stop();
                            testtts.Instance.audioSource.Stop();
                            testindex();
                        }
                        isdoubleclicked = true;
                        shakeCount = 0;
                    }
                }
                else
                {
                    doubleclickedtime = currentTime;
                    shakeCount = 0;
                   // print("doubletouchstop");
                }
            }
        }
    }



