    private const float xMin = 407.82f;//409.32f;
    private const float xMax = 452.39f;//450.855f;
    private const float yMin = 760.16f;//761.41f;
    private const float yMax = 793.73f;//789.29f;

    private const int cols = 6;  //  6
    private const int rows = 8;  //  8

    //     
    private float cellWidth = (xMax - xMin) / cols;
    private float cellHeight = (yMax - yMin) / rows;

    //    (0~47) 
    public int GetIndex(float x, float y)
    {
        int col = Mathf.Clamp((int)((x - xMin) / cellWidth), 0, cols - 1);
        int row = Mathf.Clamp((int)((y - yMin) / cellHeight), 0, rows - 1);
        return row * cols + col;  //   
    }
    //      
    public int GetCharacterIndex()
    {
        Vector3 position = TestPlayer.Instance.ts.transform.position;  //    
        return GetIndex(position.x, position.y);
    }


