    public void testindex()
    {
        if(testplayer.PV.IsMine)
        {
            if (SmartSecurity.GetInt("eventtouchindex") ==1)
            {
                SmartSecurity.SetInt("checkuserevent", 1);
                ObjPrf.Instance.pi = 0;
                ObjPrf.Instance.oi = 0;
                JoyStickTTS.Instance.audioSource.Stop();
                ObjPrf.Instance.exit();
                testplayer.allcol = Physics2D.OverlapBoxAll(testplayer.ts.transform.position, testplayer.size, 0, testplayer.alllayer);
                //  
                nearbyColliders.Clear();

                //  Collider  
                nearbyColliders.AddRange(testplayer.allcol);

                //   
                nearbyColliders.Sort((c1, c2) => {
                    float dist1 = Vector2.Distance(testplayer.ts.transform.position, c1.transform.position);
                    float dist2 = Vector2.Distance(testplayer.ts.transform.position, c2.transform.position);
                    return dist1.CompareTo(dist2);
                });

                // Collider 0   
                Collider2D collider = nearbyColliders[0]; // 
                TextController.Instance.checkobjnametts();
                if (nearbyColliders[0].CompareTag("Player")) 
                { //0   
                    var showname = nearbyColliders[0].GetComponent<TestPlayer>().tt.text; 
                    SmartSecurity.SetInt("checklisten", 1);
                    SmartSecurity.SetString("listplayerindex", showname);
                    getcharinfo(SmartSecurity.GetString("listplayerindex"));

                    //getcharinfo(showname);
                    if (SmartSecurity.GetInt("getwmsureindex") == 0)
                    {//.
                        TextController.Instance.shwocharemotionimage.sprite = TextController.Instance.avataemotionsprite[SmartSecurity.GetInt("getwitememotion")];
                        TextController.Instance.showchartopimage.sprite = TextController.Instance.wavatatopsprite[SmartSecurity.GetInt("getwitemtop")];
                        TextController.Instance.showcharfaceimage.sprite = TextController.Instance.wavatafacesprite[SmartSecurity.GetInt("getwitemface")];
                        TextController.Instance.checkcharhair();
                        if (SmartSecurity.GetInt("bleconnectindex") == 1)
                        {
                            TextController.Instance.senddotwcharimage();
                        }
                    }
                    else
                    {//.
                        TextController.Instance.shwocharemotionimage.sprite = TextController.Instance.avataemotionsprite[SmartSecurity.GetInt("getitememotion")];
                        TextController.Instance.showchartopimage.sprite = TextController.Instance.avatatopsprite[SmartSecurity.GetInt("getitemtop")];
                        TextController.Instance.showcharfaceimage.sprite = TextController.Instance.avatafacesprite[SmartSecurity.GetInt("getitemface")];
                        TextController.Instance.checkshowcharhairimage.SetActive(false);
                        if (SmartSecurity.GetInt("bleconnectindex") == 1)
                        {
                            TextController.Instance.senddotmcharimage();
                        }
                    }
                    objinfoimage.SetActive(false);
                    objpaenl.SetActive(false);
                    charinfoimage.SetActive(true);
                    charpanel.SetActive(true);
                }
                else
                {//0   
                    SmartSecurity.SetString("listname", nearbyColliders[0].name);
                    SmartSecurity.SetInt("checklisten", 2);
                    var objTt = nearbyColliders[0].GetComponent<ObjectLabel>().objTt.text;
                    var objindex = nearbyColliders[0].GetComponent<ObjectLabel>().objint;
                    SmartSecurity.SetInt("listobjindex", objindex);
                    SmartSecurity.SetString("objtts", objTt);

                    TextController.Instance.objimage.sprite = TextController.Instance.objlistimage[SmartSecurity.GetInt("listobjindex")];
                    charinfoimage.SetActive(false);
                    charpanel.SetActive(false);
                    objinfoimage.SetActive(true);
                    objpaenl.SetActive(true);
                    TextController.Instance.objlisttextpanel[SmartSecurity.GetInt("listobjindex")].SetActive(true);

                    if (SmartSecurity.GetInt("bleconnectindex") == 1)
                    {
                        if (SmartSecurity.GetInt("photonroomindex") == 1) //1
                        {

                            DisplayGraphicData(onedotimage[SmartSecurity.GetInt("listobjindex")]);
                        }
                        else if (SmartSecurity.GetInt("photonroomindex") == 2) //2
                        {
                            DisplayGraphicData(twodotimage[SmartSecurity.GetInt("listobjindex")]);
                        }
                    }
                }
                if (SmartSecurity.GetInt("ttscontrolindex") == 1)
                {
                    if (testplayer.allcol.Length > 0)
                    {
                        if (ObjPrf.Instance.usercheck == 0)
                        {
                            ObjPrf.Instance.usercheck = 1;
                            if(SmartSecurity.GetInt("checklisten") == 1)
                            {
                                testtts.Instance.alltts("  . .");
                            }
                            else if(SmartSecurity.GetInt("checklisten") == 2)
                            {
                                testtts.Instance.alltts("  . .");
                            }
                            Invoke("listuitts", 3.5f);
                        }
                        Invoke("usercheckon", 2f);
                    }
                }
            }
        }
    }

