    public void userevent()
    {
        if (SmartSecurity.GetInt("checklisten") == 1) //캐릭터 일때        
        {
            topsetting.SetActive(false);
            blindtopsetting.SetActive(true);
            blindcharnameTt.GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            blindcharinfoTt.GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            usereventclosebtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            usereventnameTt.GetComponent<AccessibilityNode>().AccessibilityLabel = "캐릭터 이름은 " + SmartSecurity.GetString("listplayerindex") + "이고 ," + "입고 있는 아이템은" + SmartSecurity.GetString("getiteminfoTt") + " 입니다.화면 하단 오른쪽에 정보 다시 듣기 버튼과 왼쪽에 이전화면으로 나가는 닫기 버튼이 있습니다.";
            charnameTt.text = SmartSecurity.GetString("listplayerindex");
            charinfoTt.text = SmartSecurity.GetString("getiteminfoTt");
            blindcharnameTt.GetComponent<AccessibilityNode>().AccessibilityLabel = SmartSecurity.GetString("listplayerindex");
            blindcharinfoTt.GetComponent<AccessibilityNode>().AccessibilityLabel = SmartSecurity.GetString("getiteminfoTt");
            userevnetlistenbtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            AccessibilityNotification.PostLayoutChangedNotification();
            Invoke("charusereventon", 2f);
        }
        else if (SmartSecurity.GetInt("checklisten") == 2) // 오브젝트 일때
        { 
            topsetting.SetActive(false);
            blindtopsetting.SetActive(true);
            usereventobjnameTt[SmartSecurity.GetInt("listobjindex")].GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            usereventobjinfoTt[SmartSecurity.GetInt("listobjindex")].GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            usereventclosebtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            userevnetlistenbtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = false;
            usereventnameTt.GetComponent<AccessibilityNode>().AccessibilityLabel = SmartSecurity.GetString("objtts")+ ".화면 하단 오른쪽에 정보 다시 듣기 버튼과 왼쪽에 이전화면으로 나가는 닫기 버튼이 있습니다.";
            AccessibilityNotification.PostLayoutChangedNotification();
            Invoke("usereventon", 2f);
        }
    }

    public void usereventon()
    {
        topsetting.SetActive(true);
        blindtopsetting.SetActive(false);
        usereventobjnameTt[SmartSecurity.GetInt("listobjindex")].GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        usereventobjinfoTt[SmartSecurity.GetInt("listobjindex")].GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        usereventclosebtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        userevnetlistenbtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        usereventnameTt.GetComponent<AccessibilityNode>().AccessibilityLabel = "유저 주변 이벤트";
        CancelInvoke("usereventon");
    }

    public void charusereventon()
    {
        topsetting.SetActive(true);
        blindtopsetting.SetActive(false);
        blindcharnameTt.GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        blindcharinfoTt.GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        usereventclosebtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        userevnetlistenbtn.GetComponent<AccessibilityNode>().IsAccessibilityElement = true;
        usereventnameTt.GetComponent<AccessibilityNode>().AccessibilityLabel = "유저 주변 이벤트";
        CancelInvoke("charusereventon");
    }


    public void listenbtn()
    {
        if (SmartSecurity.GetInt("checklisten") == 1) //캐릭터 일때        
        {
            userevnetlistenbtn.GetComponent<AccessibilityNode>().AccessibilityLabel = "듣기 버튼." + "캐릭터 이름은 " + SmartSecurity.GetString("listplayerindex") + "이고 ," + "입고 있는 아이템은" + SmartSecurity.GetString("getiteminfoTt") + " 입니다.";
            Invoke("charlistenbtnon", 1f);
        }
        else if (SmartSecurity.GetInt("checklisten") == 2) // 오브젝트 일때
        {
            userevnetlistenbtn.GetComponent<AccessibilityNode>().AccessibilityLabel = "듣기 버튼." + SmartSecurity.GetString("objtts");
            Invoke("listenbtnon", 1f);
        }
    }
    public void listenbtnon()
    {
        userevnetlistenbtn.GetComponent<AccessibilityNode>().AccessibilityLabel = "듣기 버튼";
        CancelInvoke("listenbtnon");
    }

    public void charlistenbtnon()
    {
        userevnetlistenbtn.GetComponent<AccessibilityNode>().AccessibilityLabel = "듣기 버튼";
        CancelInvoke("charlistenbtnon");
    }




