    public void objpanelimage() //전시물 이미지 보기버튼
    {
         if (SmartSecurity.GetInt("bleconnectindex") == 1)
         {
            if (SmartSecurity.GetInt("photonroomindex") == 1) //월드1층
            {
                if (AccessibilitySettings.IsVoiceOverRunning == false)
                {
                    if (SmartSecurity.GetInt("ttscontrolindex") == 1)
                    {
                        testtts.Instance.audioSource.Stop();
                        testtts.Instance.alltts("이미지보기버튼,"+SmartSecurity.GetString("objtts" + DataController.Instance.listobjindex));
                    }
                }
                DisplayGraphicData(onedotimage[SmartSecurity.GetInt("listobjindex" + DataController.Instance.listobjindex)]);
               // DisplayTextData(SmartSecurity.GetString("objtts" + DataController.Instance.listobjindex));
            }
            else if (SmartSecurity.GetInt("photonroomindex") == 2) //월드2층
            {
                DisplayGraphicData(twodotimage[SmartSecurity.GetInt("listobjindex" + DataController.Instance.listobjindex)]);
               // DisplayTextData(SmartSecurity.GetString("objtts" + DataController.Instance.listobjindex));
                if (AccessibilitySettings.IsVoiceOverRunning == false)
                {
                    if (SmartSecurity.GetInt("ttscontrolindex") == 1)
                    {
                        testtts.Instance.audioSource.Stop();
                        testtts.Instance.alltts("이미지 보기 버튼," + SmartSecurity.GetString("objtts" + DataController.Instance.listobjindex));
                    }
                }
            }
         }
         else
         {
            blindshowimagepanel.SetActive(false);
            objlistinfopanel.SetActive(true);
            objlistinfoimage.sprite = objlistimage[SmartSecurity.GetInt("listobjindex" + DataController.Instance.listobjindex)];
        }
    }

    public void objpanelimageclose() //전시물 이미지 닫기버튼
    {
        blindshowimagepanel.SetActive(true);
        if (AccessibilitySettings.IsVoiceOverRunning == false)
        {
            if (SmartSecurity.GetInt("ttscontrolindex") == 1)
            {
                testtts.Instance.audioSource.Stop();
                testtts.Instance.alltts("전시물 이미지 보기 닫기 버튼 전시물 이미지를 닫습니다");
            }
        }

    }

