    public void checkcharhair() //아바타 헤어체크
    {
        if(SmartSecurity.GetInt("getwitemface") ==0)
        {
            showcharhairimage.sprite = wavatahairsprite[0];
            checkshowcharhairimage.SetActive(true);
        }
        else if (SmartSecurity.GetInt("getwitemface") == 2)
        {
            showcharhairimage.sprite = wavatahairsprite[1];
            checkshowcharhairimage.SetActive(true);
        }
        else if (SmartSecurity.GetInt("getwitemface") == 3)
        {
            showcharhairimage.sprite = wavatahairsprite[2];
            checkshowcharhairimage.SetActive(true);
        }
        else if (SmartSecurity.GetInt("getwitemface") == 5)
        {
            showcharhairimage.sprite = wavatahairsprite[3];
            checkshowcharhairimage.SetActive(true);
        }
        else if (SmartSecurity.GetInt("getwitemface") == 6)
        {
            showcharhairimage.sprite = wavatahairsprite[4];
            checkshowcharhairimage.SetActive(true);
        }
        else if (SmartSecurity.GetInt("getwitemface") == 7)
        {
            showcharhairimage.sprite = wavatahairsprite[5];
            checkshowcharhairimage.SetActive(true);
        }
        else if (SmartSecurity.GetInt("getwitemface") == 8)
        {
            showcharhairimage.sprite = wavatahairsprite[6];
            checkshowcharhairimage.SetActive(true);
        }
        else
        {
            checkshowcharhairimage.SetActive(false);
        }

    }

    public void charinfopanelopenbtn() //유저 캐릭터정보 아바타(캐릭터) 설명하기 
    {
        //아이템설명 아바타 옷 변경 추가 .
        testtts.Instance.audioSource.Stop();
        if (SmartSecurity.GetInt("getwmsureindex") == 0) 
        {//여성.
            charinfopanelopentts(); //유저 캐릭터정보 캐릭터설명하기 TTS
            if (SmartSecurity.GetInt("bleconnectindex") == 1)
            {
                senddotwcharimage();
            }

        }
        else
        {//남성.
            charinfopanelopentts(); //유저 캐릭터정보 캐릭터설명하기 TTS
            if (SmartSecurity.GetInt("bleconnectindex") == 1)
            {
                senddotmcharimage();
            }
        }
        showcharlistenTt.text = SmartSecurity.GetString("getiteminfoTt");
        blindshowcharlistenTtobj.GetComponent<AccessibilityNode>().AccessibilityLabel = showcharlistenTt.text;
        blindcharinfopanel.SetActive(false);
        charinfopanel.SetActive(true);
    }
    public void charinfopanelopentts() //유저 캐릭터정보 캐릭터설명하기 TTS
    {
        if (AccessibilitySettings.IsVoiceOverRunning == false)
        {
            if (SmartSecurity.GetInt("ttscontrolindex") == 1)
            {
                testtts.Instance.audioSource.Stop();
                testtts.Instance.alltts("유저 캐릭터 정보 캐릭터 설명 버튼 입고 있는 아이템 설명 화면으로 이동합니다. "+"캐릭터 이름은 "+ usernameTt.text +"이고 ,"+ "입고 있는 아이템은" + SmartSecurity.GetString("getiteminfoTt")+" 입니다." );
            }
        }
    }
