    public void sceneroad() //월드 나가기 버튼
    {
        blindexitbtn.GetComponent<AccessibilityNode>().AccessibilityLabel = "월드 나가기 버튼.홈 화면으로 이동합니다";
        Invoke("exiton", 1f);
        Invoke("sceneroadon", 4f);
    }
    public void sceneroadon() //월드 나가기 n초뒤 실행 
    {
        SmartSecurity.SetInt("checkuserevent", 0);
        SmartSecurity.SetInt("loadingindex", 0);
        SmartSecurity.SetInt("checkhomeindex", 1);
        PhotonNetwork.Disconnect();
        SceneManager.LoadScene(1, LoadSceneMode.Single); //씬이 누적됨.
    }

    public void exiton()
    {
        blindexitbtn.GetComponent<AccessibilityNode>().AccessibilityLabel = "월드 나가기 버튼";
        CancelInvoke("exiton");
    }



